  1//! Admin API for account management
  2//!
  3//! Runs on a separate listener port (default 9090) and provides endpoints for
  4//! managing OAuth accounts in the pool. Not exposed via Tailscale Ingress 
  5//! accessed via `kubectl port-forward`.
  6//!
  7//! Endpoints:
  8//! - GET  /admin/accounts          list accounts with status
  9//! - POST /admin/accounts/init-oauth     start PKCE flow, return auth URL
 10//! - POST /admin/accounts/complete-oauth  exchange code, store credential, add to pool
 11//! - DELETE /admin/accounts/:id    remove account from pool + credential store
 12//! - GET  /admin/pool              pool status summary
 13
 14use std::collections::HashMap;
 15use std::sync::Arc;
 16use std::time::{Duration, Instant, SystemTime, UNIX_EPOCH};
 17
 18use axum::Router;
 19use axum::extract::{Path, State};
 20use axum::http::StatusCode;
 21use axum::response::IntoResponse;
 22use axum::routing::{delete, get, post};
 23use serde::Deserialize;
 24use tokio::sync::Mutex;
 25use tracing::{info, warn};
 26
 27use anthropic_pool::Pool;
 28
 29/// In-memory PKCE state for an in-progress OAuth flow.
 30///
 31/// Created by init-oauth and consumed by complete-oauth. Expires after
 32/// PKCE_EXPIRY_SECS to prevent stale verifiers from accumulating.
 33struct PkceState {
 34    verifier: String,
 35    created_at: Instant,
 36}
 37
 38/// Maximum age of a PKCE state entry before it expires.
 39const PKCE_EXPIRY_SECS: u64 = 600; // 10 minutes
 40
 41/// Shared state for admin API handlers.
 42#[derive(Clone)]
 43pub struct AdminState {
 44    pool: Arc<Pool>,
 45    http_client: reqwest::Client,
 46    pkce_states: Arc<Mutex<HashMap<String, PkceState>>>,
 47}
 48
 49impl AdminState {
 50    pub fn new(pool: Arc<Pool>, http_client: reqwest::Client) -> Self {
 51        Self {
 52            pool,
 53            http_client,
 54            pkce_states: Arc::new(Mutex::new(HashMap::new())),
 55        }
 56    }
 57}
 58
 59/// Build the admin axum router with all account management endpoints.
 60pub fn build_admin_router(state: AdminState) -> Router {
 61    Router::new()
 62        .route("/admin/accounts", get(list_accounts))
 63        .route("/admin/accounts/init-oauth", post(init_oauth))
 64        .route("/admin/accounts/complete-oauth", post(complete_oauth))
 65        .route("/admin/accounts/{id}", delete(delete_account))
 66        .route("/admin/pool", get(pool_status))
 67        .with_state(state)
 68}
 69
 70/// GET /admin/accounts  list all accounts with their pool status.
 71///
 72/// Never exposes tokens. Returns account IDs and their current status
 73/// (available, cooling_down, disabled).
 74async fn list_accounts(State(state): State<AdminState>) -> impl IntoResponse {
 75    let health = state.pool.health().await;
 76    let accounts = health
 77        .get("accounts")
 78        .cloned()
 79        .unwrap_or(serde_json::json!([]));
 80
 81    (
 82        StatusCode::OK,
 83        [(axum::http::header::CONTENT_TYPE, "application/json")],
 84        serde_json::json!({ "accounts": accounts }).to_string(),
 85    )
 86}
 87
 88/// POST /admin/accounts/init-oauth  generate PKCE pair and return authorization URL.
 89///
 90/// Creates a new account ID from the current unix timestamp, generates a PKCE
 91/// verifier + challenge, builds the authorization URL, and stores the verifier
 92/// in memory for complete-oauth to consume.
 93async fn init_oauth(State(state): State<AdminState>) -> impl IntoResponse {
 94    let timestamp = SystemTime::now()
 95        .duration_since(UNIX_EPOCH)
 96        .unwrap_or_default()
 97        .as_secs();
 98    let account_id = format!("claude-max-{timestamp}");
 99
100    let verifier = anthropic_auth::generate_verifier();
101    let challenge = anthropic_auth::compute_challenge(&verifier);
102    let authorization_url = anthropic_auth::build_authorization_url(&account_id, &challenge);
103
104    // Store PKCE state for complete-oauth to consume
105    let pkce_state = PkceState {
106        verifier,
107        created_at: Instant::now(),
108    };
109
110    let mut states = state.pkce_states.lock().await;
111    // Lazy cleanup: remove expired entries while holding the lock
112    states.retain(|_, s| s.created_at.elapsed().as_secs() < PKCE_EXPIRY_SECS);
113    states.insert(account_id.clone(), pkce_state);
114
115    info!(account_id, "PKCE flow initiated");
116
117    (
118        StatusCode::OK,
119        [(axum::http::header::CONTENT_TYPE, "application/json")],
120        serde_json::json!({
121            "authorization_url": authorization_url,
122            "account_id": account_id,
123            "instructions": "Open the URL in a browser, authorize, then paste the code to complete-oauth"
124        })
125        .to_string(),
126    )
127}
128
129/// Request body for complete-oauth endpoint.
130#[derive(Deserialize)]
131struct CompleteOAuthRequest {
132    account_id: String,
133    code: String,
134}
135
136/// POST /admin/accounts/complete-oauth  exchange authorization code for tokens.
137///
138/// Retrieves the PKCE verifier from the in-memory store, parses the code#state
139/// format from the callback, exchanges the code via the token endpoint, stores
140/// the credential, and adds the account to the pool.
141async fn complete_oauth(
142    State(state): State<AdminState>,
143    axum::Json(body): axum::Json<CompleteOAuthRequest>,
144) -> impl IntoResponse {
145    // Retrieve and remove PKCE state
146    let pkce_state = {
147        let mut states = state.pkce_states.lock().await;
148        states.remove(&body.account_id)
149    };
150
151    let pkce_state = match pkce_state {
152        Some(s) => s,
153        None => {
154            return (
155                StatusCode::BAD_REQUEST,
156                [(axum::http::header::CONTENT_TYPE, "application/json")],
157                serde_json::json!({
158                    "error": "no pending OAuth flow for this account_id (expired or not initiated)"
159                })
160                .to_string(),
161            );
162        }
163    };
164
165    // Check expiration
166    if pkce_state.created_at.elapsed() > Duration::from_secs(PKCE_EXPIRY_SECS) {
167        return (
168            StatusCode::BAD_REQUEST,
169            [(axum::http::header::CONTENT_TYPE, "application/json")],
170            serde_json::json!({
171                "error": "PKCE state expired (>10 minutes), please re-initiate with init-oauth"
172            })
173            .to_string(),
174        );
175    }
176
177    // Parse code#state format  the authorization code may contain '#state' suffix
178    let authorization_code = body.code.split('#').next().unwrap_or(&body.code);
179
180    // Exchange code for tokens
181    let token_response = match anthropic_auth::exchange_code(
182        &state.http_client,
183        authorization_code,
184        &pkce_state.verifier,
185    )
186    .await
187    {
188        Ok(r) => r,
189        Err(e) => {
190            warn!(account_id = body.account_id, error = %e, "token exchange failed");
191            return (
192                StatusCode::BAD_GATEWAY,
193                [(axum::http::header::CONTENT_TYPE, "application/json")],
194                serde_json::json!({
195                    "error": format!("token exchange failed: {e}")
196                })
197                .to_string(),
198            );
199        }
200    };
201
202    // Compute absolute expiration timestamp
203    let now_millis = SystemTime::now()
204        .duration_since(UNIX_EPOCH)
205        .unwrap_or_default()
206        .as_millis() as u64;
207    let expires = now_millis + (token_response.expires_in * 1000);
208
209    let credential = anthropic_auth::Credential {
210        credential_type: "oauth".to_string(),
211        refresh: token_response.refresh_token,
212        access: token_response.access_token,
213        expires,
214    };
215
216    // Store credential and add to pool
217    let credential_store = state.pool.credential_store();
218    if let Err(e) = credential_store
219        .add(body.account_id.clone(), credential)
220        .await
221    {
222        warn!(account_id = body.account_id, error = %e, "failed to store credential");
223        return (
224            StatusCode::INTERNAL_SERVER_ERROR,
225            [(axum::http::header::CONTENT_TYPE, "application/json")],
226            serde_json::json!({
227                "error": format!("failed to store credential: {e}")
228            })
229            .to_string(),
230        );
231    }
232
233    state.pool.add_account(body.account_id.clone()).await;
234
235    info!(
236        account_id = body.account_id,
237        "OAuth flow completed, account added to pool"
238    );
239
240    (
241        StatusCode::OK,
242        [(axum::http::header::CONTENT_TYPE, "application/json")],
243        serde_json::json!({
244            "account_id": body.account_id,
245            "status": "added"
246        })
247        .to_string(),
248    )
249}
250
251/// DELETE /admin/accounts/:id  remove account from pool and credential store.
252async fn delete_account(
253    State(state): State<AdminState>,
254    Path(id): Path<String>,
255) -> impl IntoResponse {
256    state.pool.remove_account(&id).await;
257
258    let credential_store = state.pool.credential_store();
259    if let Err(e) = credential_store.remove(&id).await {
260        warn!(account_id = id, error = %e, "credential removal failed (account already removed from pool)");
261    }
262
263    info!(account_id = id, "account removed");
264
265    (
266        StatusCode::OK,
267        [(axum::http::header::CONTENT_TYPE, "application/json")],
268        serde_json::json!({
269            "account_id": id,
270            "status": "removed"
271        })
272        .to_string(),
273    )
274}
275
276/// GET /admin/pool  pool status summary (same shape as health endpoint pool object).
277async fn pool_status(State(state): State<AdminState>) -> impl IntoResponse {
278    let health = state.pool.health().await;
279
280    (
281        StatusCode::OK,
282        [(axum::http::header::CONTENT_TYPE, "application/json")],
283        health.to_string(),
284    )
285}
286
287#[cfg(test)]
288mod tests {
289    use super::*;
290    use axum::body::Body;
291    use axum::http::Request;
292    use std::sync::Arc;
293    use tower::ServiceExt;
294
295    /// Create a test pool with a temporary credential store.
296    async fn test_pool(dir: &std::path::Path) -> Arc<Pool> {
297        let cred_path = dir.join("credentials.json");
298        let store = anthropic_auth::CredentialStore::load(cred_path)
299            .await
300            .unwrap();
301        let store = Arc::new(store);
302        Arc::new(Pool::new(
303            vec![],
304            Duration::from_secs(7200),
305            store,
306            reqwest::Client::new(),
307        ))
308    }
309
310    fn test_admin_state(pool: Arc<Pool>) -> AdminState {
311        AdminState::new(pool, reqwest::Client::new())
312    }
313
314    #[tokio::test]
315    async fn list_accounts_empty_pool() {
316        let dir = tempfile::tempdir().unwrap();
317        let pool = test_pool(dir.path()).await;
318        let state = test_admin_state(pool);
319        let app = build_admin_router(state);
320
321        let response = app
322            .oneshot(
323                Request::builder()
324                    .uri("/admin/accounts")
325                    .body(Body::empty())
326                    .unwrap(),
327            )
328            .await
329            .unwrap();
330
331        assert_eq!(response.status(), StatusCode::OK);
332        let body = axum::body::to_bytes(response.into_body(), 1024 * 1024)
333            .await
334            .unwrap();
335        let json: serde_json::Value = serde_json::from_slice(&body).unwrap();
336        assert_eq!(json["accounts"], serde_json::json!([]));
337    }
338
339    #[tokio::test]
340    async fn list_accounts_with_accounts() {
341        let dir = tempfile::tempdir().unwrap();
342        let pool = test_pool(dir.path()).await;
343
344        // Add a credential and account
345        let credential = anthropic_auth::Credential {
346            credential_type: "oauth".to_string(),
347            refresh: "rt_test".to_string(),
348            access: "at_test".to_string(),
349            expires: u64::MAX,
350        };
351        pool.credential_store()
352            .add("test-account".to_string(), credential)
353            .await
354            .unwrap();
355        pool.add_account("test-account".to_string()).await;
356
357        let state = test_admin_state(pool);
358        let app = build_admin_router(state);
359
360        let response = app
361            .oneshot(
362                Request::builder()
363                    .uri("/admin/accounts")
364                    .body(Body::empty())
365                    .unwrap(),
366            )
367            .await
368            .unwrap();
369
370        assert_eq!(response.status(), StatusCode::OK);
371        let body = axum::body::to_bytes(response.into_body(), 1024 * 1024)
372            .await
373            .unwrap();
374        let json: serde_json::Value = serde_json::from_slice(&body).unwrap();
375        let accounts = json["accounts"].as_array().unwrap();
376        assert_eq!(accounts.len(), 1);
377        assert_eq!(accounts[0]["id"], "test-account");
378        assert_eq!(accounts[0]["status"], "available");
379        // Verify tokens are never exposed
380        assert!(accounts[0].get("access").is_none());
381        assert!(accounts[0].get("refresh").is_none());
382    }
383
384    #[tokio::test]
385    async fn init_oauth_returns_authorization_url() {
386        let dir = tempfile::tempdir().unwrap();
387        let pool = test_pool(dir.path()).await;
388        let state = test_admin_state(pool);
389        let app = build_admin_router(state);
390
391        let response = app
392            .oneshot(
393                Request::builder()
394                    .method("POST")
395                    .uri("/admin/accounts/init-oauth")
396                    .body(Body::empty())
397                    .unwrap(),
398            )
399            .await
400            .unwrap();
401
402        assert_eq!(response.status(), StatusCode::OK);
403        let body = axum::body::to_bytes(response.into_body(), 1024 * 1024)
404            .await
405            .unwrap();
406        let json: serde_json::Value = serde_json::from_slice(&body).unwrap();
407
408        // Verify response shape
409        assert!(
410            json["authorization_url"]
411                .as_str()
412                .unwrap()
413                .starts_with("https://claude.ai/oauth/authorize")
414        );
415        assert!(
416            json["account_id"]
417                .as_str()
418                .unwrap()
419                .starts_with("claude-max-")
420        );
421        assert!(json["instructions"].as_str().is_some());
422    }
423
424    #[tokio::test]
425    async fn complete_oauth_without_init_returns_400() {
426        let dir = tempfile::tempdir().unwrap();
427        let pool = test_pool(dir.path()).await;
428        let state = test_admin_state(pool);
429        let app = build_admin_router(state);
430
431        let response = app
432            .oneshot(
433                Request::builder()
434                    .method("POST")
435                    .uri("/admin/accounts/complete-oauth")
436                    .header("content-type", "application/json")
437                    .body(Body::from(
438                        serde_json::json!({
439                            "account_id": "claude-max-999",
440                            "code": "fake-code#fake-state"
441                        })
442                        .to_string(),
443                    ))
444                    .unwrap(),
445            )
446            .await
447            .unwrap();
448
449        assert_eq!(response.status(), StatusCode::BAD_REQUEST);
450        let body = axum::body::to_bytes(response.into_body(), 1024 * 1024)
451            .await
452            .unwrap();
453        let json: serde_json::Value = serde_json::from_slice(&body).unwrap();
454        assert!(
455            json["error"]
456                .as_str()
457                .unwrap()
458                .contains("no pending OAuth flow")
459        );
460    }
461
462    #[tokio::test]
463    async fn expired_pkce_state_returns_400() {
464        let dir = tempfile::tempdir().unwrap();
465        let pool = test_pool(dir.path()).await;
466        let state = AdminState::new(pool, reqwest::Client::new());
467
468        // Manually insert an expired PKCE state
469        {
470            let mut states = state.pkce_states.lock().await;
471            states.insert(
472                "claude-max-expired".to_string(),
473                PkceState {
474                    verifier: "test-verifier".to_string(),
475                    // Set created_at far in the past
476                    created_at: Instant::now() - Duration::from_secs(PKCE_EXPIRY_SECS + 60),
477                },
478            );
479        }
480
481        let app = build_admin_router(state);
482
483        let response = app
484            .oneshot(
485                Request::builder()
486                    .method("POST")
487                    .uri("/admin/accounts/complete-oauth")
488                    .header("content-type", "application/json")
489                    .body(Body::from(
490                        serde_json::json!({
491                            "account_id": "claude-max-expired",
492                            "code": "test-code#test-state"
493                        })
494                        .to_string(),
495                    ))
496                    .unwrap(),
497            )
498            .await
499            .unwrap();
500
501        assert_eq!(response.status(), StatusCode::BAD_REQUEST);
502        let body = axum::body::to_bytes(response.into_body(), 1024 * 1024)
503            .await
504            .unwrap();
505        let json: serde_json::Value = serde_json::from_slice(&body).unwrap();
506        assert!(json["error"].as_str().unwrap().contains("expired"));
507    }
