  1//! PKCE (Proof Key for Code Exchange) implementation per RFC 7636
  2//!
  3//! Generates the code verifier and S256 challenge used during the OAuth
  4//! authorization flow. The verifier is stored server-side and sent during
  5//! token exchange; the challenge is included in the authorization URL so
  6//! the authorization server can verify the exchange request came from the
  7//! same party that initiated the flow.
  8
  9use base64::Engine;
 10use base64::engine::general_purpose::URL_SAFE_NO_PAD;
 11use rand::RngExt;
 12use sha2::{Digest, Sha256};
 13
 14use crate::constants::{ANTHROPIC_CLIENT_ID, AUTHORIZE_ENDPOINT, REDIRECT_URI, SCOPES};
 15
 16/// Generate a cryptographically random PKCE code verifier.
 17///
 18/// Produces a 128-byte random value encoded as URL-safe base64 (no padding).
 19/// RFC 7636 requires 43-128 characters; our output is 172 characters
 20/// (128 bytes * 4/3, rounded), well within the spec range.
 21pub fn generate_verifier() -> String {
 22    let mut bytes = [0u8; 128];
 23    rand::rng().fill(&mut bytes);
 24    URL_SAFE_NO_PAD.encode(bytes)
 25}
 26
 27/// Compute the S256 code challenge from a verifier.
 28///
 29/// `challenge = BASE64URL(SHA256(verifier))`
 30///
 31/// The authorization server compares this against the challenge sent in
 32/// the authorization URL to verify the token exchange request is legitimate.
 33pub fn compute_challenge(verifier: &str) -> String {
 34    let hash = Sha256::digest(verifier.as_bytes());
 35    URL_SAFE_NO_PAD.encode(hash)
 36}
 37
 38/// Build the full authorization URL with all required OAuth parameters.
 39///
 40/// The `state` parameter is an opaque value the client generates for CSRF
 41/// protection. The authorization server returns it unchanged in the callback.
 42pub fn build_authorization_url(state: &str, challenge: &str) -> String {
 43    format!(
 44        "{}?client_id={}&redirect_uri={}&response_type=code&scope={}&code_challenge={}&code_challenge_method=S256&state={}",
 45        AUTHORIZE_ENDPOINT,
 46        ANTHROPIC_CLIENT_ID,
 47        urlencoded(REDIRECT_URI),
 48        urlencoded(SCOPES),
 49        challenge,
 50        state,
 51    )
 52}
 53
 54/// Minimal URL encoding for parameter values.
 55/// Only encodes characters that would break URL parameter parsing.
 56fn urlencoded(s: &str) -> String {
 57    s.replace(' ', "%20")
 58        .replace(':', "%3A")
 59        .replace('/', "%2F")
 60}
 61
 62#[cfg(test)]
 63mod tests {
 64    use super::*;
 65
 66    #[test]
 67    fn verifier_is_url_safe_base64() {
 68        let verifier = generate_verifier();
 69        // 128 bytes  171 base64url chars (no padding, ceil(128*4/3) - 1 padding)
 70        assert_eq!(verifier.len(), 171);
 71        assert!(
 72            verifier
 73                .chars()
 74                .all(|c| c.is_ascii_alphanumeric() || c == '-' || c == '_'),
 75            "verifier must be URL-safe base64 (no padding): {verifier}"
 76        );
 77    }
 78
 79    #[test]
 80    fn verifiers_are_unique() {
 81        let a = generate_verifier();
 82        let b = generate_verifier();
 83        assert_ne!(a, b, "two verifiers must not collide");
 84    }
 85
 86    #[test]
 87    fn challenge_is_deterministic() {
 88        let verifier = "test-verifier-value";
 89        let c1 = compute_challenge(verifier);
 90        let c2 = compute_challenge(verifier);
 91        assert_eq!(c1, c2, "same verifier must produce same challenge");
 92    }
 93
 94    #[test]
 95    fn challenge_is_url_safe_base64() {
 96        let challenge = compute_challenge("test-verifier");
 97        // SHA-256 produces 32 bytes  43 base64url chars (no padding)
 98        assert_eq!(challenge.len(), 43);
 99        assert!(
100            challenge
101                .chars()
102                .all(|c| c.is_ascii_alphanumeric() || c == '-' || c == '_'),
103            "challenge must be URL-safe base64 (no padding): {challenge}"
104        );
105    }
106
107    #[test]
108    fn challenge_matches_known_value() {
109        // Pre-computed: SHA256("hello") = 2cf24dba5fb0a30e26e83b2ac5b9e29e1b161e5c1fa7425e73043362938b9824
110        // base64url of those 32 bytes = REDACTED
111        let challenge = compute_challenge("hello");
112        assert_eq!(challenge, "REDACTED");
113    }
114
115    #[test]
116    fn authorization_url_contains_required_params() {
117        let challenge = compute_challenge("test-verifier");
118        let url = build_authorization_url("test-state-123", &challenge);
119
120        assert!(url.starts_with(AUTHORIZE_ENDPOINT));
121        assert!(url.contains(&format!("client_id={ANTHROPIC_CLIENT_ID}")));
122        assert!(url.contains("response_type=code"));
123        assert!(url.contains("code_challenge_method=S256"));
124        assert!(url.contains(&format!("code_challenge={challenge}")));
125        assert!(url.contains("state=test-state-123"));
126        assert!(url.contains("scope="));
127    }
128
129    #[test]
130    fn roundtrip_verifier_challenge() {
131        // Generate a real verifier and verify the challenge is valid base64url
132        let verifier = generate_verifier();
133        let challenge = compute_challenge(&verifier);
134
135        // Decode the challenge back to verify it's valid base64url
136        let decoded = URL_SAFE_NO_PAD.decode(&challenge).expect("valid base64url");
137        assert_eq!(decoded.len(), 32, "SHA-256 hash must be 32 bytes");
138    }
139}
