337    async fn concurrent_writes_dont_corrupt() {
338        let dir = tempfile::tempdir().unwrap();
339        let path = dir.path().join("credentials.json");
340        let store = std::sync::Arc::new(CredentialStore::load(path.clone()).await.unwrap());
341
342        // Spawn multiple concurrent writes
343        let mut handles = vec![];
344        for i in 0..10 {
345            let store = store.clone();
346            handles.push(tokio::spawn(async move {
347                store
348                    .add(format!("acct-{i}"), test_credential(&i.to_string()))
349                    .await
350                    .unwrap();
351            }));
352        }
353
354        for h in handles {
355            h.await.unwrap();
356        }
357
358        // All 10 accounts should be present
359        assert_eq!(store.len().await, 10);
360
361        // File should be valid JSON
362        let contents = tokio::fs::read_to_string(&path).await.unwrap();
363        let parsed: HashMap<String, Credential> = serde_json::from_str(&contents).unwrap();
364        assert_eq!(parsed.len(), 10);
365    }
366}
