  1//! OAuth token exchange and refresh
  2//!
  3//! Handles the two token endpoint interactions:
  4//! 1. Authorization code exchange (initial OAuth flow completion)
  5//! 2. Token refresh (proactive and request-time refresh)
  6//!
  7//! Both operations POST to `TOKEN_ENDPOINT` with different grant types.
  8//! The token endpoint is Anthropic's console (`console.anthropic.com`),
  9//! not the inference API (`api.anthropic.com`).
 10
 11use serde::{Deserialize, Serialize};
 12
 13use crate::constants::{ANTHROPIC_CLIENT_ID, REDIRECT_URI, TOKEN_ENDPOINT};
 14use crate::error::{Error, Result};
 15
 16/// Response from the token endpoint for both exchange and refresh.
 17///
 18/// `expires_in` is a delta in seconds from the response time. The caller
 19/// converts this to an absolute unix millisecond timestamp when storing
 20/// the credential.
 21#[derive(Debug, Deserialize, Serialize)]
 22pub struct TokenResponse {
 23    pub access_token: String,
 24    pub refresh_token: String,
 25    /// Seconds until the access token expires (delta, not absolute)
 26    pub expires_in: u64,
 27}
 28
 29/// Exchange an authorization code for tokens (initial OAuth flow).
 30///
 31/// This is the second step of the PKCE flow: the user has authorized
 32/// in their browser, and we received the authorization code. We send
 33/// the code along with the PKCE verifier to prove we initiated the flow.
 34pub async fn exchange_code(
 35    client: &reqwest::Client,
 36    code: &str,
 37    verifier: &str,
 38) -> Result<TokenResponse> {
 39    let response = client
 40        .post(TOKEN_ENDPOINT)
 41        .form(&[
 42            ("grant_type", "authorization_code"),
 43            ("code", code),
 44            ("code_verifier", verifier),
 45            ("client_id", ANTHROPIC_CLIENT_ID),
 46            ("redirect_uri", REDIRECT_URI),
 47        ])
 48        .send()
 49        .await
 50        .map_err(|e| Error::Http(format!("token exchange request failed: {e}")))?;
 51
 52    let status = response.status();
 53    if !status.is_success() {
 54        let body = response
 55            .text()
 56            .await
 57            .unwrap_or_else(|_| String::from("<no body>"));
 58        return Err(Error::TokenExchange(format!(
 59            "token endpoint returned {status}: {body}"
 60        )));
 61    }
 62
 63    response
 64        .json::<TokenResponse>()
 65        .await
 66        .map_err(|e| Error::TokenExchange(format!("invalid token response: {e}")))
 67}
 68
 69/// Refresh an access token using a refresh token.
 70///
 71/// Called proactively by the background refresh task (before expiration)
 72/// and reactively at request time (when token is about to expire).
 73pub async fn refresh_token(client: &reqwest::Client, refresh: &str) -> Result<TokenResponse> {
 74    let response = client
 75        .post(TOKEN_ENDPOINT)
 76        .form(&[
 77            ("grant_type", "refresh_token"),
 78            ("refresh_token", refresh),
 79            ("client_id", ANTHROPIC_CLIENT_ID),
 80        ])
 81        .send()
 82        .await
 83        .map_err(|e| Error::Http(format!("token refresh request failed: {e}")))?;
 84
 85    let status = response.status();
 86    if !status.is_success() {
 87        let body = response
 88            .text()
 89            .await
 90            .unwrap_or_else(|_| String::from("<no body>"));
 91
 92        // 401/403 means the refresh token is revoked or invalid
 93        if status.as_u16() == 401 || status.as_u16() == 403 {
 94            return Err(Error::InvalidCredentials(format!(
 95                "refresh token rejected ({status}): {body}"
 96            )));
 97        }
 98
 99        return Err(Error::TokenExchange(format!(
100            "token refresh returned {status}: {body}"
101        )));
102    }
103
104    response
105        .json::<TokenResponse>()
106        .await
107        .map_err(|e| Error::TokenExchange(format!("invalid refresh response: {e}")))
108}
