100impl Config {
101    /// Determine auth mode from config shape.
102    pub fn mode(&self) -> AuthMode {
103        if self.oauth.is_some() {
104            AuthMode::OAuthPool
105        } else {
106            AuthMode::Passthrough
107        }
108    }
109
110    /// Load configuration from a TOML file, then validate.
111    pub fn load(path: &Path) -> common::Result<Self> {
112        let contents = std::fs::read_to_string(path)?;
113        let mut config: Config = toml::from_str(&contents)?;
114
115        // CREDENTIAL_FILE env var override
116        if let Ok(cred_path) = std::env::var("CREDENTIAL_FILE")
117            && let Some(ref mut oauth) = config.oauth
118        {
119            oauth.credential_file = cred_path;
120        }
121
122        // When both [oauth] and [[headers]] present, [oauth] takes precedence
123        if config.oauth.is_some() && !config.headers.is_empty() {
124            tracing::warn!(
125                "[oauth] and [[headers]] both present  [oauth] takes precedence, [[headers]] ignored"
126            );
127            config.headers.clear();
128        }
129
130        // Validate upstream_url is a parseable URL with http(s) scheme.
131        // Catches malformed URLs at startup rather than on first request.
132        let url = reqwest::Url::parse(&config.proxy.upstream_url)
133            .map_err(|e| common::Error::Config(format!("upstream_url is not a valid URL: {e}")))?;
134        match url.scheme() {
135            "http" | "https" => {}
136            scheme => {
137                return Err(common::Error::Config(format!(
138                    "upstream_url must use http or https scheme, got: {scheme}"
139                )));
140            }
141        }
142
143        // Validate timeout_secs is non-zero
144        if config.proxy.timeout_secs == 0 {
145            return Err(common::Error::Config(
146                "timeout_secs must be greater than 0".into(),
147            ));
148        }
149
150        // Validate max_connections is non-zero
151        if config.proxy.max_connections == 0 {
152            return Err(common::Error::Config(
153                "max_connections must be greater than 0".into(),
154            ));
155        }
156
157        // Validate header injection entries at load time so misconfigured
158        // headers fail fast at startup instead of being silently skipped
159        // per-request at runtime.
160        for h in &config.headers {
161            HeaderName::from_str(&h.name).map_err(|e| {
162                common::Error::Config(format!("invalid header name '{}': {e}", h.name))
163            })?;
164            HeaderValue::from_str(&h.value).map_err(|e| {
165                common::Error::Config(format!("invalid header value for '{}': {e}", h.name))
166            })?;
167        }
168
169        // Validate [oauth] fields if present
170        if let Some(ref oauth) = config.oauth {
171            if oauth.credential_file.is_empty() {
172                return Err(common::Error::Config(
173                    "oauth.credential_file must not be empty".into(),
174                ));
175            }
176            if oauth.cooldown_secs == 0 {
177                return Err(common::Error::Config(
178                    "oauth.cooldown_secs must be greater than 0".into(),
179                ));
180            }
181            if oauth.refresh_interval_secs == 0 {
182                return Err(common::Error::Config(
183                    "oauth.refresh_interval_secs must be greater than 0".into(),
184                ));
185            }
186            if oauth.refresh_threshold_secs == 0 {
187                return Err(common::Error::Config(
188                    "oauth.refresh_threshold_secs must be greater than 0".into(),
189                ));
190            }
191        }
192
193        Ok(config)
194    }
195
196    /// Resolve config file path from CLI arg or CONFIG_PATH env var.
197    pub fn resolve_path(cli_path: Option<&str>) -> PathBuf {
198        if let Some(p) = cli_path {
199            return PathBuf::from(p);
200        }
201        if let Ok(p) = std::env::var("CONFIG_PATH") {
202            return PathBuf::from(p);
203        }
204        PathBuf::from("anthropic-oauth-proxy.toml")
205    }
206}
