    if isinstance(v, str) and len(v) > 20:
        print(f'{k}: {v[:8]}...({len(v)} chars)')
    elif isinstance(v, dict):
        print(f'{k}: {{')
        for dk, dv in v.items():
            if isinstance(dv, str) and len(dv) > 20:
                print(f'  {dk}: {dv[:8]}...({len(dv)} chars)')
            else:
                print(f'  {dk}: {dv}')
        print('}')
    else:
        print(f'{k}: {v}')
" 2>&1 || echo "PARSE_FAILED"

