content.content:
  1//! Credential storage for OAuth tokens
  2//!
  3//! Manages a JSON file mapping account IDs to OAuth credentials. All writes
  4//! use atomic temp-file + rename to prevent corruption on crash. A tokio Mutex
  5//! serializes concurrent writes from request-time refresh and background refresh.
  6//!
  7//! The credential file is the single source of truth for token data. The pool
  8//! reads credentials from this store at selection time.
  9
 10use std::collections::HashMap;
 11use std::path::{Path, PathBuf};
 12
 13use serde::{Deserialize, Serialize};
 14use tokio::sync::Mutex;
 15use tracing::{debug, info};
 16
 17use crate::error::{Error, Result};
 18
 19/// A single account's OAuth credentials.
 20///
 21/// `expires` is a unix timestamp in milliseconds (absolute, not a delta).
 22/// Computed at storage time from `TokenResponse.expires_in` (seconds delta)
 23/// plus the current time.
 24#[derive(Debug, Clone, Serialize, Deserialize)]
 25pub struct Credential {
 26    /// Always "oauth" for this gateway
 27    #[serde(rename = "type")]
 28    pub credential_type: String,
 29    /// Refresh token for obtaining new access tokens
 30    pub refresh: String,
 31    /// Current access token (Bearer token for API calls)
 32    pub access: String,
 33    /// Expiration as unix timestamp in milliseconds
 34    pub expires: u64,
 35}
 36
 37/// Thread-safe credential file manager.
 38///
 39/// The Mutex serializes all writes. Reads acquire the lock briefly to clone
 40/// the in-memory state, so request-time reads don't block on background writes.
 41pub struct CredentialStore {
 42    path: PathBuf,
 43    state: Mutex<HashMap<String, Credential>>,
 44}
 45
 46impl CredentialStore {
 47    /// Load credentials from the given file path.
 48    ///
 49    /// If the file doesn't exist, creates it as `{}` (cold start with zero
 50    /// accounts). The pool will report `unhealthy` until accounts are added
 51    /// via the admin API.
 52    pub async fn load(path: PathBuf) -> Result<Self> {
 53        let state = if path.exists() {
 54            let contents = tokio::fs::read_to_string(&path)
 55                .await
 56                .map_err(|e| Error::Io(format!("reading credential file: {e}")))?;
 57            let credentials: HashMap<String, Credential> = serde_json::from_str(&contents)
 58                .map_err(|e| Error::CredentialParse(format!("parsing credential file: {e}")))?;
 59            info!(path = %path.display(), accounts = credentials.len(), "loaded credentials");
 60            credentials
 61        } else {
 62            info!(path = %path.display(), "credential file not found, starting with empty store");
 63            let store = HashMap::new();
 64            // Create the empty file so future loads don't need the cold-start path
 65            write_atomic(&path, &store).await?;
 66            store
 67        };
 68
 69        Ok(Self {
 70            path,
 71            state: Mutex::new(state),
 72        })
 73    }
 74
 75    /// Persist the current in-memory state to disk.
 76    ///
 77    /// Uses atomic write (temp file + rename) to prevent corruption.
 78    /// File permissions are set to 0600 (owner read/write only).
 79    pub async fn save(&self) -> Result<()> {
 80        let state = self.state.lock().await;
 81        write_atomic(&self.path, &state).await
 82    }
 83
 84    /// Get a clone of a specific credential.
 85    pub async fn get(&self, account_id: &str) -> Option<Credential> {
 86        let state = self.state.lock().await;
 87        state.get(account_id).cloned()
 88    }
 89
 90    /// List all account IDs.
 91    pub async fn account_ids(&self) -> Vec<String> {
 92        let state = self.state.lock().await;
 93        state.keys().cloned().collect()
 94    }
 95
 96    /// Add or replace a credential and persist to disk.
 97    pub async fn add(&self, account_id: String, credential: Credential) -> Result<()> {
 98        let mut state = self.state.lock().await;
 99        state.insert(account_id.clone(), credential);
100        debug!(account_id, "added credential");
101        write_atomic(&self.path, &state).await
102    }
103
104    /// Remove a credential and persist to disk.
105    ///
106    /// Returns the removed credential if it existed.
107    pub async fn remove(&self, account_id: &str) -> Result<Option<Credential>> {
108        let mut state = self.state.lock().await;
109        let removed = state.remove(account_id);
110        if removed.is_some() {
111            debug!(account_id, "removed credential");
112            write_atomic(&self.path, &state).await?;
113        }
114        Ok(removed)
115    }
116
117    /// Update tokens for an existing account after a refresh.
118    ///
119    /// Updates the access token, refresh token, and expiration in-memory
120    /// and persists to disk. Returns an error if the account doesn't exist.
121    pub async fn update_token(
122        &self,
123        account_id: &str,
124        access: String,
125        refresh: String,
126        expires: u64,
127    ) -> Result<()> {
128        let mut state = self.state.lock().await;
129        let credential = state.get_mut(account_id).ok_or_else(|| {
130            Error::NotFound(format!("account {account_id} not in credential store"))
131        })?;
132        credential.access = access;
133        credential.refresh = refresh;
134        credential.expires = expires;
135        debug!(account_id, "updated token");
136        write_atomic(&self.path, &state).await
137    }
138
139    /// Number of stored credentials.
140    pub async fn len(&self) -> usize {
141        let state = self.state.lock().await;
142        state.len()
143    }
144
145    /// Whether the store is empty.
146    pub async fn is_empty(&self) -> bool {
147        self.len().await == 0
148    }
149}
150
151/// Write credentials to a file atomically.
152///
153/// Writes to a temporary file in the same directory, then renames it over
154/// the target. This prevents corruption if the process crashes mid-write.
155/// Sets file permissions to 0600 (owner read/write only) since the file
156/// contains OAuth tokens.
157async fn write_atomic(path: &Path, data: &HashMap<String, Credential>) -> Result<()> {
158    let json = serde_json::to_string_pretty(data)
159        .map_err(|e| Error::CredentialParse(format!("serializing credentials: {e}")))?;
160
161    let dir = path
162        .parent()
163        .ok_or_else(|| Error::Io("credential path has no parent directory".into()))?;
164
165    let tmp_path = dir.join(format!(".credentials.tmp.{}", std::process::id()));
166
167    tokio::fs::write(&tmp_path, json.as_bytes())
168        .await
169        .map_err(|e| Error::Io(format!("writing temp credential file: {e}")))?;
170
171    // Set 0600 permissions (unix only)
172    #[cfg(unix)]
173    {
174        use std::os::unix::fs::PermissionsExt;
175        let perms = std::fs::Permissions::from_mode(0o600);
176        tokio::fs::set_permissions(&tmp_path, perms)
177            .await
178            .map_err(|e| Error::Io(format!("setting credential file permissions: {e}")))?;
179    }
180
181    tokio::fs::rename(&tmp_path, path)
182        .await
183        .map_err(|e| Error::Io(format!("renaming temp credential file: {e}")))?;
184
185    debug!(path = %path.display(), "persisted credentials");
186    Ok(())
187}
188
